  {
    "start": 58.58,
    "end": 59.62,
    "text_source": "A pen.",
    "text_target": "一支笔",
    "context": {
      "type": "dialogue",
      "summary": "简单指代一支笔",
      "grammar": "指示单个物品时使用 indefinite article 'a'",
      "cultural_note": null,
      "vocab": [
        {
          "word": "pen",
          "ipa": "/pɛn/",
          "meaning": "笔",
